import axios from 'axios';
export default {
  data() {
    return {
      query: '',
      // To store the user's search query
      loading: false,
      // To indicate whether the search is in progress
      error: null,
      // To store any error message
      gifs: [] // To store the GIF data received from the backend
    };
  },
  methods: {
    async search() {
      this.loading = true; // Set loading to true when search begins
      this.error = null; // Reset error message
      try {
        // Send a GET request to your backend API's search endpoint
        const response = await axios.get('/api/giphy/search', {
          params: {
            query: this.query
          }
        });

        // Handle the response here
        console.log('Search Results:', response.data);
        // Update the GIFs data with the search results
        this.gifs = response.data;
      } catch (error) {
        // Handle any errors here
        console.error('Error:', error);
        this.error = 'An error occurred while fetching GIFs. Please try again later.';
      } finally {
        this.loading = false; // Set loading to false when search is complete (success or error)
      }
    }
  }
};
